package main

import (
	"fmt"
)

func longestValidParentheses(s string) int {
	stack := []int{-1}
	ans := 0
	for idx, ch := range s {
		if ch == '(' {
			stack = append(stack, idx)
		} else {
			//	fmt.Println(idx, stack)
			stack = stack[:len(stack)-1]
			if len(stack) == 0 {
				stack = append(stack, idx)
			} else {
				tmp := stack[len(stack)-1]
				if idx-tmp > ans {
					ans = idx - tmp
				}
			}
		}
	}
	return ans
}

